css:
@import url('https://fonts.googleapis.com/css2?family=Cairo+Play:wght@200&display=swap');

.print-format{
	font-family: Tajawal, sans-serif !important;
	text-align:Right;
}

.print-format .row:not(.section-break) {
   	line-height: 1 !important;
	margin-top: 0px !important;
}
.head{


}

.title{
   text-align: center;
   font-size:22px;
   font-weight:bold;
}

.today{
    text-align: left; 
}

.top{
   text-align: right;
   margin-top: 12px;
}

.tables{

    margin-top: 12px;
    display: grid;
    justify-content: center;
}

.table-1{
    color:black !important;
    width:100%;
}

.table-2{
    color:black !important;
    width:100%;
}

.col-1{

    color:black !important;
    background-color: #d3d3d3 !important;
    border: 2px solid black !important;
    text-align: center;
}

.col-2{

    color:black;
    border: 2px solid black !important;
    text-align: center;
}

.print-format td, .print-format th {
        padding: 6px !important;
}

.sub_total{
    margin-top: 10px;
    display:flex;
    justify-content: space-around;
}

.box-1{
    background-color: #d3d3d3 !important;
    border:2px solid black !important;
    width:30%;
    text-align: center;
    display:flex;
    justify-content: space-around;
    padding:10px;
}

.box-2{
    background-color: #d3d3d3 !important;
    border: 2px solid black !important;
    width:30%;
    text-align: center;
    display:flex;
    justify-content: space-around;
    padding:10px;
}

.box-3{
    margin-top: 7px;
    margin-right: 190px;
    margin-left: 100px;
    background-color: #d3d3d3 !important;
    border: 2px solid black;
    width:40%;
    text-align: center;
    display:flex;
    justify-content: space-around;
    padding:10px;
}

.bottom{
    margin-top: 12px;
    border-bottom:2px solid black !important;
    border-top:2px solid black !important;
}

.footer{
    margin-top: 10px;
    margin-right: 90px;
    display:flex;
    justify-content: space-between;
    width:70%;
}
.top-section{
    border-bottom:2px solid #5C72A4;
    margin-bottom:7px;
}

.column {
  float: right!important;
  width: 30%!important;
}
.column-center{
  float: right!important;
  width: 40%!important;
}
/* Clear floats after the columns */
.row:after {
  content: ""!important;
  display: table!important;
  clear: both!important;

}
img{
    width:80%;
    padding-right:10px;
    padding-bottom:10px;
}
